parameters.jsCode:
// Calculate reporting period (previous month)
const now = new Date();
const lastMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);
const startDate = new Date(lastMonth.getFullYear(), lastMonth.getMonth(), 1);
const endDate = new Date(lastMonth.getFullYear(), lastMonth.getMonth() + 1, 0);

// Previous period for comparison
const prevStartDate = new Date(lastMonth.getFullYear(), lastMonth.getMonth() - 1, 1);
const prevEndDate = new Date(lastMonth.getFullYear(), lastMonth.getMonth(), 0);

const formatDate = (date) => date.toISOString().split('T')[0];

return {
  json: {
    reportPeriod: {
      startDate: formatDate(startDate),
      endDate: formatDate(endDate),
      month: lastMonth.toLocaleString('en-US', { month: 'long' }),
      year: lastMonth.getFullYear()
    },
    previousPeriod: {
      startDate: formatDate(prevStartDate),
      endDate: formatDate(prevEndDate)
    },
    reportDate: new Date().toISOString()
  }
};

