// Parse AI insights and prepare report data
const item = $input.first().json;
const analysisData = $('Analyze Financial Metrics').first().json;

// Parse AI response
let aiInsights;
try {
  const aiResponse = item.output || item.text || '{}';
  const cleanResponse = aiResponse.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  aiInsights = JSON.parse(cleanResponse);
} catch (e) {
  aiInsights = {
    executiveSummary: 'Financial analysis complete. Review detailed metrics below.',
    insights: ['Data successfully processed'],
    recommendations: ['Continue monitoring key metrics']
  };
}

// Company defaults
const companyData = {
  companyName: 'Media Jade',
  companyLogo: '',
  reportTitle: 'Monthly Financial Report',
  fiscalYear: new Date().getFullYear(),
  preparedBy: 'Finance Department',
  preparedByEmail: 'user@example.com',
  currency: 'USD',
  currencySymbol: '$',
  stakeholders: [
    { name: 'CEO', email: 'user@example.com' },
    { name: 'CFO', email: 'user@example.com' }
  ]
};

// Generate report ID
const date = new Date();
const reportId = `FIN-${date.getFullYear()}${String(date.getMonth() + 1).padStart(2, '0')}-${Math.floor(Math.random() * 1000)}`;

// Format dates
const formatDate = (dateStr) => {
  return new Date(dateStr).toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
};

const reportPeriod = analysisData.reportPeriod;

return {
  json: {
    ...companyData,
    ...analysisData,
    aiInsights,
    reportId,
    reportDate: formatDate(new Date().toISOString()),
    reportPeriod: {
      ...reportPeriod,
      startDateFormatted: formatDate(reportPeriod.startDate),
      endDateFormatted: formatDate(reportPeriod.endDate)
    }
  }
};

// Generate comprehensive financial report HTML
const item = $input.first().json;

const formatCurrency = (amount) => {
  return `${item.currencySymbol}${Math.abs(amount).toLocaleString('en-US', {minimumFractionDigits: 2, maximumFractionDigits: 2})}`;
};

const formatPercent = (value) => {
  return `${value >= 0 ? '+' : ''}${value.toFixed(1)}%`;
};

const getVarianceClass = (variance) => {
  if (variance > 0) return 'negative';
  if (variance < 0) return 'positive';
  return 'neutral';
};

const getHealthColor = (score) => {
  if (score >= 80) return 'success';
  if (score >= 60) return 'warning';
  return 'danger';
};

const html = `
