        .financial-table tbody tr:last-child td { border-bottom: none; }
        .text-right { text-align: right; }
        .text-bold { font-weight: 700; }
        .total-row { background: #eff6ff !important; font-weight: 700; font-size: 16px; }

        .footer { margin-top: 50px; padding-top: 25px; border-top: 3px solid #e5e7eb; text-align: center; color: #6b7280; font-size: 13px; }
        @media print { body { print-color-adjust: exact; -webkit-print-color-adjust: exact; } }
    </style>
</head>
<body>
    <div class="container">
        <div class="header">
            <h1 class="report-title">${item.reportTitle}</h1>
            <p class="report-subtitle">${item.companyName}</p>
            <p class="report-period">${item.reportPeriod.startDateFormatted} - ${item.reportPeriod.endDateFormatted}</p>
        </div>

        <div class="health-banner ${getHealthColor(item.healthScore)}">
            <div class="health-score">${item.healthScore}/100</div>
            <div class="health-status">Financial Health: ${item.healthStatus}</div>
        </div>

        <div class="metrics-grid">
