        ` : ''}

        <div class="section">
            <h2 class="section-title">Profit & Loss Statement</h2>
            <table class="financial-table">
                <thead>
                    <tr>
                        <th>Item</th>
                        <th class="text-right">Current Period</th>
                        <th class="text-right">Previous Period</th>
                        <th class="text-right">Change</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td class="text-bold">Revenue</td>
                        <td class="text-right text-bold">${formatCurrency(item.revenue)}</td>
                        <td class="text-right">${formatCurrency(item.previousPeriod.revenue)}</td>
                        <td class="text-right ${item.revenueGrowth >= 0 ? 'positive' : 'negative'}">${formatPercent(item.revenueGrowth)}</td>
                    </tr>
                    <tr>
                        <td class="text-bold">Expenses</td>
                        <td class="text-right text-bold">${formatCurrency(item.expenses)}</td>
                        <td class="text-right">${formatCurrency(item.previousPeriod.expenses)}</td>
                        <td class="text-right ${item.expenseGrowth <= 0 ? 'positive' : 'negative'}">${formatPercent(item.expenseGrowth)}</td>
                    </tr>
                    <tr class="total-row">
                        <td class="text-bold">Net Income</td>
                        <td class="text-right text-bold">${formatCurrency(item.netIncome)}</td>
                        <td class="text-right">${formatCurrency(item.previousPeriod.netIncome)}</td>
                        <td class="text-right ${item.incomeGrowth >= 0 ? 'positive' : 'negative'}">${formatPercent(item.incomeGrowth)}</td>
                    </tr>
                </tbody>
            </table>
        </div>

        ${item.categories && item.categories.length > 0 ? `
        <div class="section">
            <h2 class="section-title">Budget vs Actual Analysis</h2>
            <table class="financial-table">
                <thead>
                    <tr>
                        <th>Category</th>
                        <th class="text-right">Budget</th>
                        <th class="text-right">Actual</th>
                        <th class="text-right">Variance</th>
                        <th class="text-right">Variance %</th>
                    </tr>
                </thead>
                <tbody>
                    ${item.categories.map(cat => `
                    <tr>
                        <td>${cat.name}</td>
                        <td class="text-right">${formatCurrency(cat.budget)}</td>
                        <td class="text-right">${formatCurrency(cat.actual)}</td>
                        <td class="text-right ${getVarianceClass(cat.variance)}">${formatCurrency(cat.variance)}</td>
                        <td class="text-right ${getVarianceClass(cat.variance)}">${formatPercent(cat.variancePercent)}</td>
                    </tr>
                    `).join('')}
                </tbody>
