        <div class="footer">
            <p><strong>Report ID:</strong> ${item.reportId} | <strong>Generated:</strong> ${item.reportDate}</p>
            <p><strong>Prepared by:</strong> ${item.preparedBy} (${item.preparedByEmail})</p>
            <p style="margin-top: 15px;">This report is confidential and intended for internal use only.</p>
        </div>
    </div>
</body>
</html>
`;

return {
  json: {
    html,
    fileName: `Financial_Report_${item.reportId}_${item.reportPeriod.startDate.replace(/[^0-9]/g, '')}.pdf`,
    ...item
  }
};

