parameters.jsCode:
// Process and analyze financial data
const item = $input.first().json;

// Extract current period data
const current = item[0];
const previous = item[1];

// Calculate key metrics
const revenue = parseFloat(current.revenue) || 0;
const expenses = parseFloat(current.expenses) || 0;
const netIncome = revenue - expenses;

const prevRevenue = parseFloat(previous.revenue) || 0;
const prevExpenses = parseFloat(previous.expenses) || 0;
const prevNetIncome = prevRevenue - prevExpenses;

// Growth calculations
const revenueGrowth = prevRevenue > 0 ? ((revenue - prevRevenue) / prevRevenue * 100) : 0;
const expenseGrowth = prevExpenses > 0 ? ((expenses - prevExpenses) / prevExpenses * 100) : 0;
const incomeGrowth = prevNetIncome !== 0 ? ((netIncome - prevNetIncome) / Math.abs(prevNetIncome) * 100) : 0;

// Process expense categories
const categories = (current.categories || []).map(cat => {
  const actual = parseFloat(cat.actual) || 0;
  const budget = parseFloat(cat.budget) || 0;
  const variance = actual - budget;
  const variancePercent = budget > 0 ? (variance / budget * 100) : 0;

  return {
    name: cat.name,
    actual,
    budget,
    variance,
    variancePercent,
    isOverBudget: variance > 0
  };
});

// Identify anomalies
const anomalies = [];

if (Math.abs(revenueGrowth) > 20) {
  anomalies.push({
    type: 'revenue',
    severity: 'high',
    message: `Revenue ${revenueGrowth > 0 ? 'increased' : 'decreased'} by ${Math.abs(revenueGrowth).toFixed(1)}% (>20% threshold)`
  });
}

if (expenseGrowth > 15) {
  anomalies.push({
    type: 'expense',
    severity: 'medium',
    message: `Expenses increased by ${expenseGrowth.toFixed(1)}% (>15% threshold)`
  });
}

categories.forEach(cat => {
  if (Math.abs(cat.variancePercent) > 25) {
    anomalies.push({
      type: 'budget_variance',
      severity: 'medium',
      message: `${cat.name}: ${Math.abs(cat.variancePercent).toFixed(1)}% ${cat.isOverBudget ? 'over' : 'under'} budget`
    });
  }
});

// Calculate health score (0-100)
let healthScore = 50;
if (netIncome > 0) healthScore += 20;
if (revenueGrowth > 0) healthScore += 15;
if (expenseGrowth < 5) healthScore += 10;
if (anomalies.length === 0) healthScore += 5;

const healthStatus = healthScore >= 80 ? 'Excellent' : 
                     healthScore >= 60 ? 'Good' : 
                     healthScore >= 40 ? 'Fair' : 'Concerning';

return {
  json: {
    reportPeriod: item[0].reportPeriod || { startDate: '', endDate: '' },
    revenue,
    expenses,
    netIncome,
    previousPeriod: {
      revenue: prevRevenue,
      expenses: prevExpenses,
      netIncome: prevNetIncome
    },
    revenueGrowth,
    expenseGrowth,
    incomeGrowth,
    grossMargin: revenue > 0 ? ((revenue - expenses) / revenue * 100) : 0,
    categories,
    anomalies,
    anomalyCount: anomalies.length,
    healthScore,
    healthStatus,
    requiresReview: anomalies.length > 2 || healthScore < 50
  }
};
